function init(){car=$(".car").ThreeSixty({totalFrames:52,endFrame:52,currentFrame:1,imgList:".threesixty_images",progress:".spinner",imagePath:"assets/images/spins",filePrefix:"",ext:".jpg",height:1e3,width:447,navigation:!1}),$(".custom_previous").bind("click",function(i){car.previous()}),$(".custom_next").bind("click",function(i){car.next()}),$(".custom_play").bind("click",function(i){car.play()}),$(".custom_stop").bind("click",function(i){car.stop()})}window.onload=init;var car;